import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const StartPage = () => {
  const navigate = useNavigate();

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  // Check if the user is already logged in by checking localStorage
  useEffect(() => {
    const storedUserId = localStorage.getItem('loggedInUserId');
    if (storedUserId) {
      // If user data is found in localStorage, navigate directly to the overview page
      navigate('/overview');
    }
  }, [navigate]);

  // Handle login form submission
  const handleLogin = async () => {
    console.log('Login button clicked');

    // Log the username and password to verify they're correct
    console.log('Username:', username);
    console.log('Password:', password);

    try {
      // Make API request to verify username and password (use the correct /login endpoint)
      const response = await fetch('http://127.0.0.1:5000/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();
      console.log('Backend response:', data);

      if (response.ok) {
        // Store user data with a unique key based on user ID
        const userKey = `user_${data.userId}`;
        localStorage.setItem(userKey, JSON.stringify({ username, userId: data.userId }));

        // Store the userId to track which user is logged in
        localStorage.setItem('loggedInUserId', data.userId);

        // Navigate to overview page
        navigate('/overview');
      } else {
        setError(data.error || 'Invalid credentials');
      }
    } catch (err) {
      console.error('Error during login request:', err);
      setError('Something went wrong. Please try again later.');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      <h1 className="text-4xl font-bold mb-8">Login to Budget.ly</h1>

      {/* Display error if credentials are invalid */}
      {error && <p className="text-red-500">{error}</p>}

      <div className="mb-4">
        <input
          type="text"
          placeholder="Username"
          className="mb-4 p-2 border rounded w-64"
          value={username}
          onChange={(e) => setUsername(e.target.value)} 
        />
        <input
          type="password"
          placeholder="Password"
          className="mb-4 p-2 border rounded w-64"
          value={password}
          onChange={(e) => setPassword(e.target.value)} 
        />
      </div>

      <button
        onClick={handleLogin}
        className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
      >
        Login
      </button>
    </div>
  );
};

export default StartPage;
