import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const StartPage = () => {
  const navigate = useNavigate();

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  // Handle login form submission
  const handleLogin = async () => {
    // Make API request to verify username and password
    const response = await fetch('http://localhost:5000/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
    });

    const data = await response.json();

    if (response.ok) {
      // Store user data and navigate to overview page
      localStorage.setItem('username', username);
      localStorage.setItem('user_id', data.userId);  // Assuming the response includes the user ID
      navigate('/overview');
    } else {
      setError(data.error || 'Invalid credentials');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      <h1 className="text-4xl font-bold mb-8">Login to Budget.ly</h1>

      {/* Display error if credentials are invalid */}
      {error && <p className="text-red-500">{error}</p>}

      <div className="mb-4">
        <input
          type="text"
          placeholder="Username"
          className="mb-4 p-2 border rounded w-64"
          value={username}
          onChange={(e) => setUsername(e.target.value)} 
        />
        <input
          type="password"
          placeholder="Password"
          className="mb-4 p-2 border rounded w-64"
          value={password}
          onChange={(e) => setPassword(e.target.value)} 
        />
      </div>

      <button
        onClick={handleLogin}
        className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
      >
        Login
      </button>
    </div>
  );
};

export default StartPage;
