import React, { useState, useEffect } from 'react';
import Sidebar from './Sidebar.tsx';

const SavingsGoalProgress = () => {
  const [goalAmount, setGoalAmount] = useState(0);
  const [savedAmount, setSavedAmount] = useState(0);
  const [newGoalAmount, setNewGoalAmount] = useState(0);
  const [newSavedAmount, setNewSavedAmount] = useState(0);
  const [userId, setUserId] = useState('');

  // Fetch the userId from localStorage to retrieve the correct user data
  useEffect(() => {
    const storedUserId = localStorage.getItem('loggedInUserId');
    if (storedUserId) {
      setUserId(storedUserId);  // Set the userId based on the logged-in user
    } else {
      // If no userId found, redirect to the login page
      window.location.href = '/';
    }
  }, []);

  // Fetch the current goal and progress from localStorage for the logged-in user
  useEffect(() => {
    if (userId) {
      const storedUserGoalAmount = localStorage.getItem(`user_${userId}_goalAmount`);
      const storedUserSavedAmount = localStorage.getItem(`user_${userId}_savedAmount`);

      if (storedUserGoalAmount && storedUserSavedAmount) {
        setGoalAmount(parseFloat(storedUserGoalAmount));
        setSavedAmount(parseFloat(storedUserSavedAmount));
      }
    }
  }, [userId]);

  // Handle the submission of a new savings goal
  const handleSaveGoal = () => {
    // Save new goal and saved amount to localStorage for the specific user
    if (userId) {
      localStorage.setItem(`user_${userId}_goalAmount`, newGoalAmount.toString());
      localStorage.setItem(`user_${userId}_savedAmount`, newSavedAmount.toString());

      // Update the state with the new values
      setGoalAmount(newGoalAmount);
      setSavedAmount(newSavedAmount);

      alert('Savings goal set successfully!');
    }
  };

  const progress = goalAmount && savedAmount ? (savedAmount / goalAmount) * 100 : 0;

  return (
    <div className="flex h-screen">
      <Sidebar /> {/* Add Sidebar here */}

      <div className="flex-1 bg-gray-100 overflow-y-auto">
        <div className="p-6 bg-white rounded shadow-lg">
          <h3 className="text-2xl font-semibold mb-4">Savings Goal Progress</h3>
          <div className="mb-4">
            <p className="text-lg">Goal: ${goalAmount}</p>
            <p className="text-lg">Saved: ${savedAmount}</p>
          </div>

          {/* Progress Bar */}
          <div className="w-full bg-gray-200 h-4 rounded mb-2">
            <div
              className="h-4 bg-green-500 rounded"
              style={{ width: `${Math.min(progress, 100)}%` }}
            ></div>
          </div>

          <p className="text-sm text-gray-500">{Math.round(progress)}% saved</p>

          <div className="mt-6">
            <h4 className="text-lg font-semibold">Set New Savings Goal</h4>
            <input
              type="number"
              placeholder="Enter new goal amount"
              className="w-full p-2 border rounded mb-4"
              value={newGoalAmount}
              onChange={(e) => setNewGoalAmount(parseFloat(e.target.value))}
            />
            <h4 className="text-lg font-semibold">Amount Saved of Savings Goal</h4>
            <input
              type="number"
              placeholder="Enter saved amount"
              className="w-full p-2 border rounded mb-4"
              value={newSavedAmount}
              onChange={(e) => setNewSavedAmount(parseFloat(e.target.value))}
            />
            <button
              onClick={handleSaveGoal}
              className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
            >
              Save New Goal
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SavingsGoalProgress;
