import React, { useState, useEffect } from 'react';
import Sidebar from './Sidebar.tsx';

const SavingsGoalProgress = () => {
  const [goalAmount, setGoalAmount] = useState(0);
  const [savedAmount, setSavedAmount] = useState(0);
  const [newGoalAmount, setNewGoalAmount] = useState(0);
  const [newSavedAmount, setNewSavedAmount] = useState(0);
  const [budget, setBudget] = useState(1000); // Default budget, can be fetched from localStorage

  // Fetch the current goal and progress from your API or static data
  useEffect(() => {
    fetch('http://127.0.0.1:5000/savings-goal', {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' },
    })
      .then((res) => res.json())
      .then((data) => {
        setGoalAmount(data.goalAmount);
        setSavedAmount(data.savedAmount);
      })
      .catch((err) => console.error('Error fetching savings goal:', err));
  }, []);

  // Fetch the budget from localStorage when the component mounts
  useEffect(() => {
    const storedBudget = localStorage.getItem('budget');
    if (storedBudget) {
      setBudget(parseFloat(storedBudget)); // Set the budget from localStorage
    }
    // Optionally, you could also fetch the budget from the backend here if needed
    // fetch('http://127.0.0.1:5000/budget')
    //   .then(res => res.json())
    //   .then(data => setBudget(data.amount));
  }, []);

  // Handle the submission of a new savings goal
  const handleSaveGoal = () => {
    if (newGoalAmount && newSavedAmount) {
      // Update the budget by subtracting the saved amount
      const updatedBudget = budget - newSavedAmount;
      setBudget(updatedBudget); // Update the budget state

      // Update the savings goal in the backend
      fetch('http://127.0.0.1:5000/savings-goal', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          goalAmount: newGoalAmount,
          savedAmount: newSavedAmount,
        }),
      })
        .then((res) => res.json())
        .then((data) => {
          setGoalAmount(newGoalAmount);
          setSavedAmount(newSavedAmount);
          alert('Savings goal set successfully!');
        })
        .catch((err) => {
          console.error('Error setting savings goal:', err);
          alert('Failed to set savings goal');
        });
    } else {
      alert("Please fill in both the goal amount and saved amount.");
    }
  };

  // Calculate progress towards savings goal
  const progress = (savedAmount / goalAmount) * 100;

  return (
    <div className="flex h-screen">
      <Sidebar /> {/* Sidebar for navigation */}

      <div className="flex-1 bg-gray-100 overflow-y-auto">
        <div className="p-6 bg-white rounded shadow-lg">
          <h3 className="text-2xl font-semibold mb-4">Savings Goal Progress</h3>
          <div className="mb-4">
            <p className="text-lg">Goal: ${goalAmount}</p>
            <p className="text-lg">Saved: ${savedAmount}</p>
            <p className="text-lg">Remaining Budget: ${budget}</p> {/* Display updated remaining budget */}
          </div>

          {/* Progress Bar */}
          <div className="w-full bg-gray-200 h-4 rounded mb-2">
            <div
              className="h-4 bg-green-500 rounded"
              style={{ width: `${Math.min(progress, 100)}%` }} 
            ></div>
          </div>

          <p className="text-sm text-gray-500">{Math.round(progress)}% saved</p>

          <div className="mt-6">
            <h4 className="text-lg font-semibold mb-4">Set New Savings Goal</h4>

            {/* Goal Amount Input */}
            <div className="mb-4">
              <label htmlFor="goalAmount" className="block text-sm font-semibold text-gray-700">Enter your new goal amount:</label>
              <input
                id="goalAmount"
                type="number"
                placeholder="Enter goal amount here"
                className="w-full p-2 border border-gray-300 rounded mb-4"
                value={newGoalAmount}
                onChange={(e) => setNewGoalAmount(parseFloat(e.target.value))}
              />
            </div>

            {/* Saved Amount Input */}
            <div className="mb-4">
              <label htmlFor="savedAmount" className="block text-sm font-semibold text-gray-700">Enter how much has been saved so far:</label>
              <input
                id="savedAmount"
                type="number"
                placeholder="Enter saved amount here"
                className="w-full p-2 border border-gray-300 rounded mb-4"
                value={newSavedAmount}
                onChange={(e) => setNewSavedAmount(parseFloat(e.target.value))}
              />
            </div>

            {/* Save Button */}
            <button
              onClick={handleSaveGoal}
              className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
            >
              Save New Goal
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SavingsGoalProgress;
