import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Sidebar from './Sidebar.tsx';

const Budget = () => {
  const [income, setIncome] = useState('');
  const [showSuccess, setShowSuccess] = useState(false);
  const [username, setUsername] = useState('');
  const [userId, setUserId] = useState('');
  const navigate = useNavigate();

  // Check if user is logged in by checking localStorage
  useEffect(() => {
    const storedUserId = localStorage.getItem('loggedInUserId');
    const storedUsername = localStorage.getItem(`user_${storedUserId}`);

    if (storedUserId && storedUsername) {
      // If username and userId exist in localStorage, retrieve them
      setUsername(storedUsername);
      setUserId(storedUserId);
    } else {
      // If no username found in localStorage, redirect to login page
      navigate('/');
    }
  }, [navigate]);

  // Submit budget function
  const submitBudget = () => {
    if (!income || isNaN(parseFloat(income))) {
      alert("Please enter a valid income.");
      return;
    }

    fetch('http://127.0.0.1:5000/budget', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ 
        amount: parseFloat(income), 
        userId: userId // Pass the userId to associate the budget with the correct user
      })
    })
    .then(res => res.json())
    .then(data => {
      console.log('Budget response:', data);

      // Animate confirmation message
      setShowSuccess(true);

      // After 1 second, redirect to "/sheet"
      setTimeout(() => {
        setShowSuccess(false);
        navigate('/sheet');
      }, 1000);
    })
    .catch(error => {
      console.error('Error setting budget:', error);
      alert('Failed to set budget');
    });
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="w-64">
        <Sidebar />
      </div>

      {/* Main Content */}
      <div className="flex flex-col flex-1 bg-gray-100">
        {/* Top Section with Title and Subtitle */}
        <div className="h-48 bg-gray-500 text-white shadow-lg py-8">
          <h1 className="text-5xl text-center font-semibold">Input Monthly Budget</h1>
          <p className="mt-4 text-3xl text-center text-gray-200">
            Follow these steps to create a custom monthly budget
          </p>
        </div>

        {/* User Greeting */}
        <div className="h-16 bg-gray-200 text-gray-700 py-4">
          {username && (
            <h2 className="text-2xl text-center font-semibold">Hello, {username}!</h2>
          )}
        </div>

        {/* First step is to set income */}
        <div className="h-48 bg-gray-100 text-gray-500 py-8">
          <h1 className="text-3xl text-center font-semibold">1: Set your monthly income</h1>
        </div>
        <div className="flex justify-center">
          <input
            type="text"
            placeholder="Enter income here"
            className="relative -translate-y-20 w-64 p-2 rounded border border-gray-300"
            value={income}
            onChange={(e) => setIncome(e.target.value)}
          />
        </div>

        {/* Submit Budget Button */}
        <div className="flex justify-center mt-4">
          <button
            onClick={submitBudget}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Submit Budget
          </button>
        </div>

        {/* Confirmation animation after submitting budget */}
        {showSuccess && (
          <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-green-500 text-white px-6 py-4 rounded-lg shadow-lg transition-all duration-500">
            Thanks for entering your budget! Now enter your expenses...
          </div>
        )}
      </div>
    </div>
  );
};

export default Budget;
