import React, { useEffect, useState } from 'react';
import { PieChart, Pie, Cell, Legend } from 'recharts';

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

const MainBudget = () => {

  //state to store the budget summary 
  const [summary, setSummary] = useState({
    budget: 0,
    total_spent: 0,
    remaining: 0,
    breakdown: {}
  });

  // fetch budget summary from backend when component mounts
  useEffect(() => {
    fetch('http://127.0.0.1:5000/summary')
      .then(res => res.json())
      .then(data => {
        setSummary(data);
      })
      .catch(err => {
        console.error('Error fetching summary:', err);
      });
  }, []);


  //transform breakdown object into array for the pie chart
  const data = Object.entries(summary.breakdown).map(([name, value]) => ({
    name,
    value
  }));

  return (
    <div className="flex flex-col items-center my-8">
      <PieChart width={300} height={300}>
        <Pie
          data={data}
          cx="50%"
          cy="50%"
          innerRadius={80}
          outerRadius={120}
          label
          fill="#8884d8"
          dataKey="value"
        >
          {data.map((entry, index) => (
            <Cell key={index} fill={COLORS[index % COLORS.length]} />
          ))}
        </Pie>
      </PieChart>

    <Legend
      layout="horizontal"
      align="center"
      verticalAlign="bottom"
      iconType="circle"
    />
  </div>
  );
};

  export default MainBudget;
